/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: #5a5a5a;
  --font-primary: "Bespoke Serif", serif;
  --font-secondary: "Open Sans", sans-serif;

  /* Base values */
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --border-color: #cbcace;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--font-secondary), system-ui, sans-serif;
  color: #222;
  line-height: 1.5;
  font-size: 1.125rem;
  background: white;
}

/* Elements */
.section-container {
  max-width: 900px;
  margin: 0 auto;
  padding: 4rem 2rem;
}

a.link {
  line-height: 1.25;
  font-weight: 400;
  border-bottom: 2px solid var(--color-accent, rebeccapurple);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-family: var(--font-primary);
  font-size: 2rem;
  line-height: 1.15;
  font-weight: 500;
  margin-bottom: 1.25rem;
}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 5px;
  padding: 8px 20px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

/* Content Section */
.section .content {
  max-width: 900px;
  margin: 0 auto;
  padding: 4rem 2rem;

  p {
    padding: 0.25rem 0;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  a.link {
  }

  h1 {
    font-family: var(--font-primary);
    font-size: 3rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  h2 {
    font-family: var(--font-primary);
    font-size: 2rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  h3 {
    font-family: var(--font-primary);
    font-size: 1.5rem;
    font-weight: 500;
    margin-bottom: 0.5rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}

.section-container { /* Defined in Site CSS */
  padding-bottom: 1rem; 
}

.primary {
  color: var(--color-text-secondary); 
  padding-bottom: 4rem; 

  a.email {
    display: flex; 
    align-items: center;

    span {
      margin-left: 0.75rem;
    }

    :global(svg) {
      transition: 0.2s transform;
    }

    &:hover {
      :global(svg) {
        transform: translateX(5px);
      }
    }
  }
}

.secondary { 
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  gap: 1rem;
  padding-top: 1rem;
  padding-bottom: 1rem;
}

.copyright {
  font-size: 0.75rem; 
  color: var(--color-text-secondary, #2D3339); 
}

.primo {
  font-weight: 600;
  font-size: 0.75rem;
  a {
    text-decoration: underline;  
  }
}

ul {
  display: flex;
  gap: 1.5rem;
  font-size: 1.5rem;
  list-style: none;

  a {
    transition: var(--transition);

    &:hover {
      color: var(--color-accent);
    }
  }
}

@media (min-width: 600px) {
  .secondary {  
    flex-direction: row;
  }

  ul {
    font-size: 1.25rem;
  }
}

